(()=>{"use strict";(e=>{document.getElementById("timer-days");const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let m;m=setInterval((()=>{let e=(()=>{let e=(new Date("22 september 2022").getTime()-(new Date).getTime())/1e3,t=Math.floor(e/60/60),n=Math.floor(e/60%60),o=Math.floor(e%60);return t=t<10?"0"+t:t,n=n<10?"0"+n:n,o=o<10?"0"+o:o,{timeRemaining:e,hours:t,minutes:n,seconds:o}})();t.textContent=e.hours,n.textContent=e.minutes,o.textContent=e.seconds,e.timeRemaining<0&&(clearInterval(m),t.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})()})();